import './App.css';
import React, { useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import { Container, Segment, Grid, Header, Image } from 'semantic-ui-react';
import MatchCard from './matchCard';
import useAxios from './useAxios';

const LiveScores = (props) => {
    const [todaysFixtures, setTodaysFixtures] = useState([]);
    const [inPlay, setInPlay] = useState(false);
    // const [timer, setTimer] = useState(new Date());

    const [ data, isPending, error ] = useAxios;

    useEffect(() => {
        if (todaysFixtures.length === 0){
            props.fixtures.map(fixture => {
            if (new Date(fixture.event_date).getDate() === new Date().getDate()){
                setTodaysFixtures(oldArr => [...oldArr, fixture]);
                }
            })
        }

        props.fixtures.map(fixture => {
            if (fixture.statusShort === '1H' | 'HT' | '2H') {
                setInPlay(true);
            }
        })
        console.log(inPlay);
    },[todaysFixtures]);

    useEffect(() => {


    },[])

    const renderScores = todaysFixtures.map(fixture => {
        return <MatchCard fixture={fixture}/>
    })

    return(
       <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                <Segment style={{margin: '15px'}} textAlign='center'>
                    <Grid>
                        <Grid.Column width={4}/>
                        <Grid.Column width={8}>
                            <Header style={{color: '#37003C'}}>
                                Today's Games
                            </Header>
                            <Image style={{display: 'block', marginLeft: 'auto', marginRight: 'auto'}} src='images/premier-league-3-logo.png'></Image>
                        </Grid.Column>
                        <Grid.Column width={4}/>
                    </Grid>
                </Segment>
                <Container>
                    {todaysFixtures.length === 0 && <Segment style={{margin: '15px'}} textAlign='center'><div>No games today</div><Link to='/'>Fixtures/Results</Link></Segment>}
                    {todaysFixtures.length >= 1 && <Container>{renderScores}</Container>}
                </Container>
            </Container>   
       </Container>
    )  
}

export default LiveScores;
