import './App.css';
import React, { useEffect, useState, useRef } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Container, Menu, Image } from 'semantic-ui-react';

import fixturesData from '../apis/fixturesData';
import Home from './home';
import LeagueTable from './leagueTable';

const App = () => {
    // state = {}

    // handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    const [round, setRound] = useState('');
    const [fixtures, setFixtures] = useState([]);
    const [table, setTable] = useState([]);

    const prevRound = useRef(round);

    useEffect(() => {
        if (round !== prevRound) {
            fetchFixtures();
        } else {
            fixturesData.get('fixtures/rounds/2790/current').then((res) => {
                let currentRound = res.data.api.fixtures[0].slice(-2);
                if (currentRound[0] === '_') {
                    currentRound = currentRound[1];
                }
                setRound(currentRound);
                }).catch(function (error) {
                    console.log(error);
                }).then(() => {
                    fetchFixtures();
                })
        }

            fixturesData.get('leagueTable/2790').then((res) => {
                setTable(res.data.api.standings[0])
                }).catch(function (error) {
                console.log(error);
            })
    });

    const fetchFixtures = () => {
        fixturesData.get(`fixtures/league/2790/Regular_Season_-_${round}`).then((res) => {
            setFixtures(res.data.api.fixtures);
            }).catch(function (error) {
            console.log(error);
        })
    }

    const updateRound = (round) => {
        setRound(round);
    }


        // const { activeItem } = this.state

    return(
        <Router>
            <Menu inverted size='huge' style={{ backgroundColor: '#37003C', margin: '0rem', borderRadius: '0rem'}}>
              <Container>
                  <Menu.Item></Menu.Item>
                  <Menu.Item header style={{ color: 'white' }}><Image size='tiny' src='/images/PL-Lion.png'/> PremInfo</Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Fixtures/Results'
                    //   active={activeItem === 'Fixtures/Results'}
                    //   onClick={this.handleItemClick}
                  >
                  <Link to='/'>Fixtures/Results</Link>
                  </Menu.Item>
                  <Menu.Item
                    style={{ color: 'white' }}
                    name='Table'
                    // active={activeItem === 'Table'}
                    // onClick={this.handleItemClick}
                  >
                  <Link to='/leaguetable'>Table</Link>
                  </Menu.Item>
              </Container>
            </Menu>
            <Switch>
                  <Route exact path="/"><Home round={round} fixtures={fixtures} updateRound={updateRound}/></Route>
                  <Route exact path="/leaguetable"><LeagueTable table={table}/></Route>
            </Switch>
        </Router>
    )  
}

export default App;
