import { useEffect, useState } from 'react';

const GoogleAuth = () => {
    const [ signedIn, setSignedIn ] = useState(null);
    const [ user, setUser ] = useState(null);

    useEffect(() => {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '938718032559-c7ed870afhfngepnv87e5u1jv8dmpb71.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                const auth = window.gapi.auth2.getAuthInstance();
                setSignedIn(auth.isSignedIn.get());
                auth.isSignedIn.listen(onAuthChange);
            })
        });
    }, [signedIn])

    const onAuthChange = () => {
        setSignedIn(window.gapi.auth2.getAuthInstance().isSignedIn.get());
        setUser(window.gapi.auth2.getAuthInstance().currentUser.get().getBasicProfile());
    }

    const onSignIn = () => {
        window.gapi.auth2.getAuthInstance().signIn();
    }

    const onSignOut = () => {
        window.gapi.auth2.getAuthInstance().signOut();
        setUser(null);
    }

    return(
        <div signedIn={signedIn} user={user}>
            {!signedIn && <div onClick={onSignIn}>Sign In</div>}
            {signedIn && <div onClick={onSignOut}>Sign Out</div>}
        </div>
    )
}

export default GoogleAuth;
