import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Header, Segment, Image, Grid, GridColumn, Divider, Button, Comment } from 'semantic-ui-react';

import PageHeader from './pageHeader';
import NewsComment from './newsComment';

const NewsArticle = () => {
    const { id } = useParams();
    const [article, setArticle] = useState(null);

    useEffect(() => {
        fetch('http://localhost:8000/news/' + id)
        .then(res => {
            return res.json();
        })
        .then(data => {
            setArticle(data);
        });
    }, [])

    return (
        <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                {article &&
                    <Segment style={{margin: '15px'}}>
                        <Grid>
                        <GridColumn width={1}/>
                                <Grid.Column width={14} style={{padding: '30px 0'}}>
                                    <Header as='h5'>{article.date}</Header>
                                    <Divider/>
                                    <Header as='h1' style={{color: '#37003C', textAlign: 'center'}}>{article.title}</Header>
                                    <Header as='h3'>{article.header}</Header>
                                    <Image size='huge' src={article.image} style={{display: 'block', margin: '40px auto'}}/>
                                    <p style={{marginTop: '15px'}}>{article.body}</p>
                                    <Divider/>
                                    <Header as='h3' style={{color: '#37003C'}}>Comments</Header>
                                    <Comment.Group>
                                        {article.comments.map(comment => {return <NewsComment/>})}
                                    </Comment.Group>
                                        <Button position='right' size='small' style={{backgroundColor: '#37003C', color: 'white'}}>Add Comment</Button>


                                </Grid.Column>
                            <GridColumn width={1}/>
                        </Grid>
                    </Segment>
                }
           </Container>
       </Container>
    )
}

export default NewsArticle
