import './App.css';
import React from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Container } from 'semantic-ui-react';

import fixtures from '../apis/fixtures';
import MatchData from './matchData';

class App extends React.Component {
    state = { round: '', fixtures: [] }

    componentDidMount() {
        this.getRound().then(() => {
            this.getFixtures();
            console.log(this.state);
        })
        return this.getFixtures;
    }

    getRound = () => {
        fixtures.get('fixtures/rounds/2790/current').then((res) => {
            this.setState({ round: res.data.api.fixtures[0]} );
        }).catch(function (error) {
            console.log(error);
        });
    }

    getFixtures = () => {
        fixtures.get(`fixtures/league/2790/${this.state.round}`).then((res) => {
            this.setState({ fixtures: res.data.api.fixtures });
        }).catch(function (error) {
            console.log(error);
        })
    };

    render() { 
        const { fixtures } = this.state;

        return(
           <Container className="App">
               <MatchData/>
           </Container>
        )  
    }
}

// fixtures.map(fixture => { return (
