import './App.css';
import React, { useEffect, useState} from 'react';
import Moment from 'moment';
import { Link } from 'react-router-dom';
import { Container, Segment, Grid, Header, Image } from 'semantic-ui-react';
import MatchCard from './matchCard';
import useAxios from './useAxios';

const LiveScores = (props) => {
    const [todaysFixtures, setTodaysFixtures] = useState([]);
    const [inPlay, setInPlay] = useState(false);
    // const [timer, setTimer] = useState(new Date());

    const { data: todaysGames, isPending, error } = useAxios(`fixtures/league/2790/${new Date().toISOString().slice(0,10)}`);

    useEffect(() => {
        if (todaysFixtures.length === 0){
            props.fixtures.map(fixture => {
            if (new Date(fixture.event_date).getDate() === new Date().getDate()){
                setTodaysFixtures(oldArr => [...oldArr, fixture]);
                }
            })
        }

        props.fixtures.map(fixture => {
            if (fixture.statusShort === '1H' | 'HT' | '2H') {
                setInPlay(true);
            }
        })
