import './App.css';
import React, { useEffect, useState, useRef } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Container, Menu, Image } from 'semantic-ui-react';

import fixturesData from '../apis/fixturesData';
import Home from './home';
import LeagueTable from './leagueTable';
import LiveScores from './liveScores';
import News from './news';
import TopScorers from './topScorers';
import useAxios from './useAxios';

const App = () => {
    const { data: current, isPending, error } = useAxios('fixtures/rounds/2790/current');
    const [round, setRound] = useState('');
    const [fixtures, setFixtures] = useState([]);
    const prevRound = useRef(round);

    useEffect(() => {
        formatRound()
            .then(() => {
                fetchFixtures();
            })      
    },[round, current, data])

    const formatRound = () => {
        if (isPending === true || error){
            console.log(isPending, error);
        } else if (round !== prevRound.current) {
            fetchFixtures();
        } else {
            let currentRound = current.data.api.fixtures[0].slice(-2);
            if(currentRound[0] === '_'){
                currentRound = currentRound[1];
            } 
            setRound(currentRound);
            prevRound.current = currentRound;
        }
    }

    const fetchFixtures = () => {
        fixturesData.get(`fixtures/league/2790/Regular_Season_-_${round}`).then((res) => {
            setFixtures(res.data.api.fixtures);
            }).catch(function (error) {
            console.log(error);
        })
    }




