import './App.css';
import React from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Container } from 'semantic-ui-react';

import fixtures from '../apis/fixtures';
import MatchData from './matchData';
import FixtureList from './fixtureList';


class App extends React.Component {
    state = { round: '', fixtures: [] }

    componentDidMount() {
        fixtures.get('fixtures/rounds/2790/current').then((res) => {
            this.setState({ round: res.data.api.fixtures[0]} );
        }).catch(function (error) {
            console.log(error);
        }).then(() => {
            fixtures.get(`fixtures/league/2790/${this.state.round}`).then((res) => {
                this.setState({ fixtures: res.data.api.fixtures });
                }).catch(function (error) {
                console.log(error);
            })
        })
    }

    render() {
        return(
           <Container className="App">
               <MatchData round={this.state.round}>
                   <FixtureList fixtures={this.state.fixtures}></FixtureList>
               </MatchData>
           </Container>
        )  
    }
}

// fixtures.map(fixture => { return (
            //     <div>{fixture.referee}</div>
            // )}
            // )

export default App;
