import '../App.css';
import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Segment } from 'semantic-ui-react';
import MatchCard from '../fixturesResults/matchCard';
import PageHeader from '../pageHeader';
import useAxios from '../../apis/useAxios';

const LiveScores = () => {
    const scores = useAxios(`fixtures/league/3456/${new Date().toISOString().slice(0,10)}`, 86400000);

    return(
       <Container className="App">
           <Container className='border'>
                <PageHeader title={"Today's Games"}/>
                <Container>                  
                    { scores.error && <Segment style={{margin: '15px'}} textAlign='center'><div>{scores.error}</div></Segment> }
                    { scores.isPending && console.log(scores.isPending) }
                    {/* { scores.data.api.fixtures.length === 0 && <Segment style={{margin: '15px'}} textAlign='center'><div>No games today</div><Link to='/'>Fixtures/Results</Link></Segment>}
                    { scores.data.api.fixtures && scores.data.api.fixtures.map(fixture => {return <MatchCard fixture={fixture} key={fixture.fixture_id}/>})} */}
                </Container>
            </Container>   
       </Container>
    )  
}

export default LiveScores;

