import { useEffect, useState } from 'react';

const GoogleAuth = () => {
    const [ signedIn, setSignedIn ] = useState(null);
    const [ auth, setAuth ] = useState(null);

    useEffect(() => {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '938718032559-c7ed870afhfngepnv87e5u1jv8dmpb71.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                setAuth(window.gapi.auth2.getAuthInstance());
                setSignedIn(auth.signedIn.get())
                auth.signedIn.listen(onAuthChange);
            })

        });
    }, [isSignedIn, auth])

    const onAuthChange = () => {
        setSignedIn(auth.signedIn.get())
    }

    return(
        <div>
            {!isSignedIn && <div>Sign In</div>}
            {isSignedIn && <div>Sign Out</div>}
        </div>
    )
}

export default GoogleAuth;
