import React from 'react';
import { Grid, Segment, Header, Image, } from 'semantic-ui-react';

const MatchCard = ({ fixture }) => {
    console.log(fixture);

    const fixtureDate = () => {
        const date = new Date(fixture.event_date);
        const dateString = date.toLocaleString('en-gb', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'});
        return dateString;
    }

    const score = () => {
        const date = new Date(fixture.event_date);
        const time = date.toLocaleTimeString('en-gb', { hour: '2-digit', minute: '2-digit' });

        if (fixture.statusShort === 'FT') {
            return <div style={{backgroundColor: 'red'}}>`${fixture.goalsHomeTeam} : ${fixture.goalsAwayTeam}`</div>;
        } else if (fixture.statusShort === 'NS'){
            return <div style={{backgroundColor: 'green'}}>{time}</div>;
        } else if (fixture.statusShort === 'PST') {
            return <div style={{backgroundColor: 'yellow'}}>Postponed</div>;
        }
    }

    return (
    <Segment style={{margin: '15px'}} textAlign='center'>
        <Grid>
            <Grid.Column width={4}>
                <Image size="tiny" src={fixture.homeTeam.logo} centered style={{paddingTop: '15px'}}></Image>
            </Grid.Column>
            <Grid.Column width={8} textAlign='center' >
                    <Header as="h3" style={{color: '#37003C'}}>{fixtureDate()}</Header>
                    <Header>
                        <Grid style={{color: '#37003C'}}>
                            <Grid.Column width={6}>{fixture.homeTeam.team_name}</Grid.Column>
                            <Grid.Column width={4} >{score()}</Grid.Column>
                            <Grid.Column width={6}>{fixture.awayTeam.team_name}</Grid.Column>
                        </Grid>   
                    </Header>
                    <Header as="h4" style={{color: '#37003C'}}>
                        <Grid>
                            <Grid.Column width={7}>Venue: {fixture.venue}</Grid.Column>
                            <Grid.Column width={2}></Grid.Column>
                            <Grid.Column width={7}>Referee: {fixture.referee}</Grid.Column>
                        </Grid>
                    </Header>
            </Grid.Column>
            <Grid.Column width={4}>
                <Image size="tiny" src={fixture.awayTeam.logo} centered style={{paddingTop: '15px'}}></Image>
            </Grid.Column>
        </Grid>
    </Segment>
    )
}

export default MatchCard;
