import React from 'react';
import { Link } from 'react-router-dom';
import { Menu, Image, Container } from 'semantic-ui-react';
import GoogleAuthContextProvider from '../apis/googleAuthContext';
import GoogleAuth from '../apis/googleAuth';

class Navbar extends React.Component {
    state = { activeItem: 'Fixtures/Results' }

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem } = this.state;

    return (
        <Menu inverted size='huge' style={{ backgroundColor: '#37003C', margin: '0rem', borderRadius: '0rem'}}>
              <Container>
                  <Menu.Item header style={{ color: 'white' }}><Image size='tiny' src='/images/PL-Lion.png'/> PremInfo</Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Live Scores'
                      active={activeItem === 'Livescores'}
                      onClick={this.handleItemClick}
                  >
                  <Link to='/livescores'>Live Scores</Link>
                  </Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='News'
                      active={activeItem === 'news'}
                      onClick={this.handleItemClick}
                  >
                  <Link to='/news'>News</Link>
                  </Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Fixtures/Results'
                      active={activeItem === 'Fixtures/Results'}
                      onClick={this.handleItemClick}
                  >
                  <Link to='/'>Fixtures/Results</Link>
                  </Menu.Item>
                  <Menu.Item
                    style={{ color: 'white' }}
                    name='Table'
                    active={activeItem === 'Table'}
                    onClick={this.handleItemClick}
                  >
                  <Link to='/leaguetable'>Table</Link>
                  </Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Top Scorers'
                      active={activeItem === 'Top Scorers'}
                      onClick={this.handleItemClick}
                  >
                  <Link to='/topscorers'>Top Scorers</Link>
                  </Menu.Item>
                  <Menu.Item
                      position="right"
                      style={{ color: 'white' }}
                      name='Sign in'
                      active={activeItem === 'Sign in'}
                      onClick={this.handleItemClick}
                  >
                  <GoogleAuthContextProvider>
                  <GoogleAuth/>
                  </GoogleAuthContextProvider>
                  </Menu.Item>
              </Container>
            </Menu>
        )
    }
}

export default Navbar;
