import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container } from 'semantic-ui-react';

import PageHeader from './pageHeader';

const NewsArticle = () => {
    const { id } = useParams();
    const [article, setArticle] = useState(null);

    useEffect(() => {
        fetch('http://localhost:8000/news/' + id)
        .then(res => {
            return res.json();
        })
        .then(data => {
            setArticle(data);
        });
    }, [])

    return (
        <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                <PageHeader title='title'/>
                <Segment style={{margin: '15px'}} textAlign='center'>

                </Segment>
           </Container>
       </Container>
    )
}

export default NewsArticle
