import React from 'react';
import { Container, Segment, Header, Image, Grid, Button, Icon } from 'semantic-ui-react';
import MatchCard from './matchCard';
import useAxios from './useAxios';

const FixtureList = (props) => {
    const { data, isPending, error } = useAxios(`fixtures/league/2790/Regular_Season_-_${props.round}`);

    const fixtures = data.data.api.fixtures.map(fixture => {
        return <MatchCard fixture={fixture} />
    });

    const roundMinusOne = () => {
        return parseInt(props.round) - 1;
    }

    const roundPlusOne = () => {
        return parseInt(props.round) + 1;
    }

    return (
        <div>
            <div>
            <Segment style={{margin: '15px'}} textAlign='center'>
                <Grid>
                    <Grid.Column width={4}> 
                        <Button icon labelPosition='left' style={{marginTop: '60px', backgroundColor: '#37003C', color: 'white'}} onClick={() => props.updateRound(roundMinusOne().toString())}>
                            Game Week {parseInt(props.round) - 1}
                            <Icon name='left arrow' />
                        </Button>  
                    </Grid.Column>
                    <Grid.Column width={8}>
                        <Header style={{color: '#37003C'}}>
                            Game Week {props.round}
                        </Header>
                        <Image style={{display: 'block', marginLeft: 'auto', marginRight: 'auto'}} src={premLogo}></Image>
                    </Grid.Column>
                    <Grid.Column width={4}>
                        <Button icon labelPosition='right' style={{marginTop: '60px', backgroundColor: '#37003C', color: 'white'}} onClick={() => props.updateRound(roundPlusOne().toString())}>
                            Game Week {parseInt(props.round) + 1}
                            <Icon name='right arrow' />
                        </Button>
                    </Grid.Column>
                </Grid>
            </Segment>
            </div>
            <div>
                <Container>{fixtures}</Container>
            </div>
            <div>
                <Segment style={{margin: '15px'}} textAlign='center'>
                    <Grid>
                        <Grid.Column width={4}>
                            <Button icon labelPosition='left' style={{marginTop: '30px', backgroundColor: '#37003C', color: 'white'}} onClick={() => props.updateRound(roundMinusOne().toString())}>
                                Game Week {parseInt(props.round) - 1}
                                <Icon name='left arrow' />
                            </Button>
                        </Grid.Column>
                        <Grid.Column width={8}>
                            <Image style={{display: 'block', marginLeft: 'auto', marginRight: 'auto'}} src={premLogo}/>
                        </Grid.Column>
                        <Grid.Column width={4}>
                            <Button icon labelPosition='right' style={{marginTop: '30px', backgroundColor: '#37003C', color: 'white'}} onClick={() => props.updateRound(roundPlusOne().toString())}>
                                Game Week {parseInt(props.round) + 1}
                                <Icon name='right arrow' />
                            </Button>
                        </Grid.Column>
                    </Grid>
                </Segment>
            </div>
        </div>  
     );
}

export default FixtureList;
