import './App.css';
import React, { useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import { Container, Segment, Grid, Header, Image } from 'semantic-ui-react';
import MatchCard from './matchCard';
import useAxios from './useAxios';

const LiveScores = (props) => {
    const [inPlay, setInPlay] = useState(false);
    // const [timer, setTimer] = useState(new Date());

    const { data: todaysGames, isPending, error } = useAxios(`fixtures/league/2790/${new Date().toISOString().slice(0,10)}`);

    return(
       <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                <Segment style={{margin: '15px'}} textAlign='center'>
                    <Grid>
                        <Grid.Column width={4}/>
                        <Grid.Column width={8}>
                            <Header style={{color: '#37003C'}}>
                                Today's Games
                            </Header>
                            <Image style={{display: 'block', marginLeft: 'auto', marginRight: 'auto'}} src='images/premier-league-3-logo.png'></Image>
                        </Grid.Column>
                        <Grid.Column width={4}/>
                    </Grid>
                </Segment>
                <Container>                  
                    { error && <Segment style={{margin: '15px'}} textAlign='center'><div>{error}</div></Segment> }
                    { isPending && <Segment style={{margin: '15px'}} textAlign='center'><div>Loading...</div></Segment> }
                    { !todaysGames && <Segment style={{margin: '15px'}} textAlign='center'><div>No games today</div><Link to='/'>Fixtures/Results</Link></Segment>}
                    { todaysGames && todaysGames.data.api.fixtures.map(fixture => {return <MatchCard fixture={fixture}/>})}
                </Container>
            </Container>   
       </Container>
    )  
}

export default LiveScores;
