import './App.css';
import React, { useEffect, useState, useRef } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import GoogleAuthContextProvider from '../apis/googleAuthContext';

import fixturesData from '../apis/fixturesData';
import useAxios from '../apis/useAxios';
import Navbar from './routes/navbar';
import Home from './fixturesResults/home';
import LiveScores from './liveScores/liveScores';
import News from './news/news';
import NewsArticle from './news/newsArticle';
import LeagueTable from './leagueTable/leagueTable';


const App = () => {
    const { data: table, isPending: tablePending, error: tableError } 
          = useAxios('leagueTable/3456', 86400000);
    const { data: todaysGames, isPending: todaysGamesPending, error: todaysGamesError } 
          = useAxios(`fixtures/league/3456/${new Date().toISOString().slice(0,10)}`, 86400000);
    const [round, setRound] = useState('');
    const [fixtures, setFixtures] = useState([]);
    const prevRound = useRef(round);

    const [rounds, setRounds] = useState([])
    // const newFixtures = () => {
    //     for (let i = 1; i < 38; i++){
    //         fixturesData.get(`fixtures/league/3456/Regular_Season_-_${i}`).then((res) => {
    //             setRounds(rounds.push(res.data.api.fixtures));
    //         }).catch(function (error) {
    //             console.log(error);
    //     })
    //     console.log(rounds);
    // }

    useEffect(() => {

        for (let i = 1; i < 38; i++){
            fixturesData.get(`fixtures/league/3456/Regular_Season_-_${i}`).then((res) => {
                setRounds(rounds.push(res.data.api.fixtures));
            }).catch(function (error) {
                console.log(error);
           });
        }
        console.log(rounds);

        const fetchFixtures = () => {
            fixturesData.get(`fixtures/league/3456/Regular_Season_-_${round}`).then((res) => {
                setFixtures(res.data.api.fixtures);
                }).catch(function (error) {
                console.log(error);
            })
        }

        if (round !== prevRound.current) {
                fetchFixtures();
            } else {
            fixturesData.get('fixtures/rounds/3456/current').then((res) => {
                let currentRound = res.data.api.fixtures[0].slice(-2);
                if (currentRound[0] === '_') {
                    currentRound = currentRound[1];
                }
                setRound(currentRound);
                prevRound.current = currentRound;
                }).catch(function (error) {
                    console.log(error);
                }).then(() => {
                    fetchFixtures();
                })
        }

    },[round, todaysGames, table])

    const updateRound = (round) => {
        setRound(round);
    }

    return(
        <Router>
            <Navbar/>
            <Switch>
                  <Route exact path="/"><Home round={round} fixtures={fixtures} updateRound={updateRound}/></Route>
                  <Route exact path="/livescores"><LiveScores todaysGames={todaysGames} todaysGamesPending={todaysGamesPending} todaysGamesError={todaysGamesError}/></Route>
                  <Route exact path="/news"><News/></Route>
                  <Route exact path="/news/:id">
                      <GoogleAuthContextProvider>
                        <NewsArticle/>
                      </GoogleAuthContextProvider>
                  </Route>
                  <Route exact path="/leaguetable"><LeagueTable table={table} tablePending={tablePending} tableError={tableError}/></Route>
            </Switch>
        </Router>
    )  
}

export default App;


