import '../App.css';
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Container, Segment } from 'semantic-ui-react';
import MatchCard from '../fixturesResults/matchCard';
import PageHeader from '../pageHeader';

const LiveScores = (props) => {
    const { todaysGames, todaysGamesPending, todaysGamesError } = props

    return(
       <Container className="App">
           <Container className='border'>
                <PageHeader title={"Today's Games"}/>
                <Container>
                    { todaysGamesError && <Segment style={{margin: '15px'}} textAlign='center'><div>{todaysGamesError}</div></Segment> }
                    { todaysGamesPending && <Segment style={{margin: '15px'}} textAlign='center'><div>Loading...</div></Segment> }
                    { todaysGames && <div>{todaysGames.data.api.fixtures.length}</div>}
                    { todaysGames.data.api.fixtures.length === 0 && <Segment style={{margin: '15px'}} textAlign='center'><div>No games today</div><Link to='/'>Fixtures/Results</Link></Segment>}
                    { todaysGames.data.api.fixtures && todaysGames.data.api.fixtures.map(fixture => {return <MatchCard fixture={fixture} key={fixture.fixture_id}/>})}                
                    {/* { props.todaysGamesError && <Segment style={{margin: '15px'}} textAlign='center'><div>{props.todaysGamesError}</div></Segment> } */}
                    {/* { props.todaysGamesPending && <Segment style={{margin: '15px'}} textAlign='center'><div>Loading...</div></Segment> } */}
                    {/* { props.todaysGames && <div>{props.todaysGames.data.api.fixtures.length}</div>} */}
                    {/* { props.todaysGames.data.api.fixtures.length === 0 && <Segment style={{margin: '15px'}} textAlign='center'><div>No games today</div><Link to='/'>Fixtures/Results</Link></Segment>} */}
                    {/* { props.todaysGames.data.api.fixtures && props.todaysGames.data.api.fixtures.map(fixture => {return <MatchCard fixture={fixture} key={fixture.fixture_id}/>})} */}
                </Container>
            </Container>   
       </Container>
    )  
}

export default LiveScores;
