import '../App.css';
import { Container, Table, Segment } from 'semantic-ui-react';

import PageHeader from '../pageHeader';
import TableRow from './tableRow';

const LeagueTable = (props) => {
    console.log(props)
    return (
        <Container className="App">
            <Container className='border'>
            <PageHeader title={'League Table'} />
            <Segment style={{margin: '15px', padding: '0px', border: '0px'}}>
            <Table unstackable striped>
                <Table.Header>
                  <Table.Row textAlign='center'>
                    <Table.HeaderCell>Rank</Table.HeaderCell>
                    <Table.HeaderCell></Table.HeaderCell>
                    <Table.HeaderCell textAlign='left'>Club</Table.HeaderCell>
                    <Table.HeaderCell>Played</Table.HeaderCell>
                    <Table.HeaderCell>Won</Table.HeaderCell>
                    <Table.HeaderCell>Drawn</Table.HeaderCell>
                    <Table.HeaderCell>Lost</Table.HeaderCell>
                    <Table.HeaderCell>GF</Table.HeaderCell>
                    <Table.HeaderCell>GA</Table.HeaderCell>
                    <Table.HeaderCell>GD</Table.HeaderCell>
                    <Table.HeaderCell>Points</Table.HeaderCell>
                  </Table.Row>
                </Table.Header>
                <Table.Body>
                  { props.tableError && <tr><td>{props.tableError}</td></tr> }
                  { props.tablePending && <tr><td>Loading...</td></tr> }
                  { props.table && props.table.data.api.standings[0].map(row => {return <TableRow row={row} key={row.rank}/>})}
                </Table.Body>
            </Table>
            </Segment>
            </Container>
        </Container>
    );
}

export default LeagueTable;
