import React from 'react';

class Home extends React.Component {
    state = { round: '', fixtures: [], table: [] };

    componentDidMount() {
        fixtures.get('fixtures/rounds/2790/current').then((res) => {
               let round = res.data.api.fixtures[0].slice(-2);
               if (round[0] === '_') {
                   round = round[1];
               }
            this.setState({ round } );
        }).catch(function (error) {
            console.log(error);
        }).then(() => {
            this.fetchFixtures();
        })

        fixtures.get('leagueTable/2790').then((res) => {
            this.setState({ table: res.data.api.standings })
        }).catch(function (error) {
            console.log(error);
        })
    }

    componentDidUpdate(prevProps, prevState) {
        if(this.state.round !== prevState.round){
            this.fetchFixtures();
        }
    }

    fetchFixtures = () => {
        fixtures.get(`fixtures/league/2790/Regular_Season_-_${this.state.round}`).then((res) => {
            this.setState({ fixtures: res.data.api.fixtures });
            }).catch(function (error) {
            console.log(error);
        })
    }

    updateRound = (round) => {
        this.setState({ round: round});
    }

    render() {
        return(
           <Container className="App">
                <MatchData fixtures={this.state.fixtures} round={this.state.round} updateRound={this.updateRound}/>
           </Container>
        )  
    }
}

export default Home;
