import React from 'react';
import { Grid, Segment, Header, Image, } from 'semantic-ui-react';

const MatchCard = ({ fixture }) => {
    console.log(fixture);

    const fixtureDate = () => {
        let date = new Date(fixture.event_date);
        let dateString = date.toLocaleString('en-gb', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'});
        let time = date.toLocaleTimeString('en-gb', { hour: '2-digit', minute: '2-digit' })
        return `${dateString}, ${time}`;
    }

    return (
    <Segment style={{margin: '15px'}} textAlign='center'>
        <Grid>
            <Grid.Column width={4}>
                <Image size="tiny" src={fixture.homeTeam.logo} centered style={{paddingTop: '15px'}}></Image>
            </Grid.Column>
            <Grid.Column width={8} textAlign='center' style={{color: '#37003C'}}>
                    <Header as="h3">{fixtureDate()}</Header>
                    <Header>
                        <Grid>
                            <Grid.Column width={7}>{fixture.homeTeam.team_name}</Grid.Column>
                            <Grid.Column width={2}>12:30</Grid.Column>
                            <Grid.Column width={7}>{fixture.awayTeam.team_name}</Grid.Column>
                        </Grid>   
                    </Header>
                    <Header as="h4">
                        <Grid>
                            <Grid.Column width={7}>Venue: {fixture.venue}</Grid.Column>
                            <Grid.Column width={2}></Grid.Column>
                            <Grid.Column width={7}>Referee: {fixture.referee}</Grid.Column>
                        </Grid>
                    </Header>
            </Grid.Column>
            <Grid.Column width={4}>
                <Image size="tiny" src={fixture.awayTeam.logo} centered style={{paddingTop: '15px'}}></Image>
            </Grid.Column>
        </Grid>
    </Segment>
    )
}

export default MatchCard;
