import React from 'react';
import { Container, Table, Segment, Grid, Image, Header } from 'semantic-ui-react';

import TableRow from './tableRow';
import useAxios from './useAxios';

const TableTemplate = () => {
    const {data, isPending, error } = useAxios('leagueTable/2790');
    const table = data.data.api.standings[0];

    return (
        <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
            <Segment style={{margin: '15px'}} textAlign='center'>
                <Grid>
                    <Grid.Column width={4}> 
                    </Grid.Column>
                    <Grid.Column width={8}>
                        <Header style={{color: '#37003C'}}>
                            League Table
                        </Header>
                        <Image style={{display: 'block', marginLeft: 'auto', marginRight: 'auto'}} src='images/premier-league-3-logo.png'></Image>
                    </Grid.Column>
                    <Grid.Column width={4}>
                    </Grid.Column>
                </Grid>
            </Segment>

            <Segment style={{margin: '15px', padding: '0px', border: '0px'}}>
            <Table striped>
                <Table.Header>
                  <Table.Row textAlign='center'>
                    <Table.HeaderCell>Rank</Table.HeaderCell>
                    <Table.HeaderCell></Table.HeaderCell>
                    <Table.HeaderCell textAlign='left'>Club</Table.HeaderCell>
                    <Table.HeaderCell>Played</Table.HeaderCell>
                    <Table.HeaderCell>Won</Table.HeaderCell>
                    <Table.HeaderCell>Drawn</Table.HeaderCell>
                    <Table.HeaderCell>Lost</Table.HeaderCell>
                    <Table.HeaderCell>GF</Table.HeaderCell>
                    <Table.HeaderCell>GA</Table.HeaderCell>
                    <Table.HeaderCell>GD</Table.HeaderCell>
                    <Table.HeaderCell>Points</Table.HeaderCell>
                  </Table.Row>
                </Table.Header>

                <Table.Body>
                  { error && <div>{error}</div> }
                  { isPending && <div>Loading...</div> }
                  { table && console.log(table)}
                </Table.Body>
            </Table>
            </Segment>
            </Container>
    );
}

export default TableTemplate;

