import { useEffect, useState, createContext } from 'react';

export const GoogleAuthContext = createContext();

const GoogleAuthContextProvider = (props) => {
    const [ signedIn, setSignedIn ] = useState(false);
    const [ userGivenName, setUserGivenName ] = useState(null);
    const [ userId, setUserId ] = useState(null);

    useEffect(() => {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '938718032559-c7ed870afhfngepnv87e5u1jv8dmpb71.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                const auth = window.gapi.auth2.getAuthInstance();
                setSignedIn(auth.isSignedIn.get());
                auth.isSignedIn.listen(onAuthChange);
            })
        });
    }, [signedIn])

    const onAuthChange = () => {
        setSignedIn(window.gapi.auth2.getAuthInstance().isSignedIn.get());
        !userGivenName ? setUserGivenName(window.gapi.auth2.getAuthInstance().currentUser.get().getBasicProfile().getGivenName()) : setUserGivenName(null);
        setUserId(window.gapi.auth2.getAuthInstance().currentUser.get().getBasicProfile().getId());
    }

    const onSignIn = () => {
        window.gapi.auth2.getAuthInstance().signIn();
    }

    const onSignOut = () => {
        window.gapi.auth2.getAuthInstance().signOut();
    }

    return(
        <GoogleAuthContext.Provider value={{signedIn, userGivenName, userId, onSignIn, onSignOut}}>
            {props.children}
        </GoogleAuthContext.Provider>
    )
}

export default GoogleAuthContextProvider;
