import './App.css';
import React from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Container } from 'semantic-ui-react';

import fixtures from '../apis/fixtures';
import MatchData from './matchData';

class App extends React.Component {
    state = { round: '', fixtures: [] };

    componentDidMount() {
        fixtures.get('fixtures/rounds/2790/current').then((res) => {
            if (!this.state.round) {
                let round = res.data.api.fixtures[0].slice(-2);
                if (round[0] === '_') {
                   round = round[1];
                }
                this.setState({ round } );
            }
        }).catch(function (error) {
            console.log(error);
        }).then(() => {
            fixtures.get(`fixtures/league/2790/Regular_Season_-_${this.state.round}`).then((res) => {
                this.setState({ fixtures: res.data.api.fixtures });
                }).catch(function (error) {
                console.log(error);
            })
        })
    }

    updateRound = (round) => {
        this.setState({ round: round});
    }

    render() {
        console.log(this.state.fixtures)
        return(
           <Container className="App">
                <MatchData fixtures={this.state.fixtures} round={this.state.round} updateRound={this.updateRound}/>             
           </Container>
        )  
    }
}

export default App;
