import './App.css';
import React from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Container } from 'semantic-ui-react';

import fixtures from '../apis/fixtures';

class App extends React.Component {
    state = { round: '', fixtures: [], table: [] };

    componentDidMount() {
        fixtures.get('fixtures/rounds/2790/current').then((res) => {
               let round = res.data.api.fixtures[0].slice(-2);
               if (round[0] === '_') {
                   round = round[1];
               }
            this.setState({ round } );
        }).catch(function (error) {
            console.log(error);
        }).then(() => {
            this.fetchFixtures();
        })

        fixtures.get('leagueTable/2790').then((res) => {
            this.setState({ table: res.data.api.standings })
        }).catch(function (error) {
            console.log(error);
        })
    }

    componentDidUpdate(prevProps, prevState) {
        if(this.state.round !== prevState.round){
            this.fetchFixtures();
        }
    }

    fetchFixtures = () => {
        fixtures.get(`fixtures/league/2790/Regular_Season_-_${this.state.round}`).then((res) => {
            this.setState({ fixtures: res.data.api.fixtures });
            }).catch(function (error) {
            console.log(error);
        })
    }

    updateRound = (round) => {
        this.setState({ round: round});
    }

    render() {
        return(
            <Router>
            <Menu inverted style={{ backgroundColor: '#37003C'}}>
              <Container>
                  <Menu.Item header style={{ color: 'white' }}>PremInfo</Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Fixtures/Results'
                      active={activeItem === 'Fixtures/Results'}
                      onClick={this.handleItemClick}
                  >
                  <Link to='/'>Fixtures/Results</Link>
                  </Menu.Item>
                  <Menu.Item
                    style={{ color: 'white' }}
                    name='Table'
                    active={activeItem === 'Table'}
                    onClick={this.handleItemClick}
                  >
                  <Link to='/leaguetable'>Table</Link>
                  </Menu.Item>
              </Container>
            </Menu>
            <Switch>
                  <Route exact path="/" component={Home} />
                  <Route exact path="/leaguetable" component={LeagueTable} />
            </Switch>
          </Router>
        )  
    }
}

export default App;
