import './App.css';
import React, { useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import { Container, Segment, Grid, Header, Image } from 'semantic-ui-react';
import MatchCard from './matchCard';
import MatchData from './matchData';

const LiveScores = (props) => {
    const [todaysFixtures, setTodaysFixtures] = useState([]);

    useEffect(() => {
        if (!todaysFixtures){
            props.fixtures.map(fixture => {
                if (new Date(fixture.event_date).getDate() === new Date().getDate()){
                    setTodaysFixtures(oldArr => [...oldArr, fixture]);
                } 
            })
        }
    },[todaysFixtures]);

    const renderScores = () => {
        console.log(todaysFixtures);
        if (!todaysFixtures) {
            return (
                <Segment style={{margin: '15px'}} textAlign='center'>
                    No Games today
                    <Link to='/'>Fixtures and Results</Link>
                </Segment>
            )
        } else {
            todaysFixtures.map(fixture => {
                return <MatchCard fixture={fixture}/>
            })
        }
    }

    return(
       <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                <Segment style={{margin: '15px'}} textAlign='center'>
                    <Grid>
                        <Grid.Column width={4}/>
                        <Grid.Column width={8}>
                            <Header style={{color: '#37003C'}}>
                                Live Scores
                            </Header>
                            <Image style={{display: 'block', marginLeft: 'auto', marginRight: 'auto'}} src='images/premier-league-3-logo.png'></Image>
                        </Grid.Column>
                        <Grid.Column width={4}/>
                    </Grid>
                </Segment>
            </Container>
            <Container>
                <Container>{renderScores}</Container>
            </Container>
       </Container>
    )  
}

export default LiveScores;
