import React, { useEffect, useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Header, Segment, Image, Grid, GridColumn, Divider, Button, Comment, Form } from 'semantic-ui-react';

import NewsComment from './newsComment';

const NewsArticle = () => {
    const { signedIn, user } = useContext(GoogleAuthContext);
    const { id } = useParams();
    const [article, setArticle] = useState(null);
    const [author, setAuthor] = useState(null);
    const [authorId, setAuthorId] = useState(null);
    const [comment, setComment] = useState('');

    useEffect(() => {
        fetch('http://localhost:8000/news/' + id)
        .then(res => {
            return res.json();
        })
        .then(data => {
            setArticle(data);
        });

        const signedIn = window.gapi.auth2.getAuthInstance().isSignedIn.get();
        if (signedIn) {
            setAuthor(window.gapi.auth2.getAuthInstance().currentUser.get().getBasicProfile().getGivenName());
            setAuthorId(window.gapi.auth2.getAuthInstance().currentUser.get().getBasicProfile().getId());
        }
    }, [])

    const handleSubmit = (e) => {
        e.preventDefault();
        const signedIn = window.gapi.auth2.getAuthInstance().isSignedIn.get();
        if (signedIn) {
        const newComment = { author, authorId, date: new Date(), comment }
        console.log(newComment);
        }
    }

    return (
        <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                {article &&
                    <Segment style={{margin: '15px'}}>
                        <Grid>
                        <GridColumn width={1}/>
                        <Grid.Column width={14} style={{padding: '30px 0'}}>
                            <Header as='h5'>{article.date}</Header>
                            <Divider/>
                            <Header as='h1' style={{color: '#37003C', textAlign: 'center'}}>{article.title}</Header>
                            <Header as='h3'>{article.header}</Header>
                            <Image size='huge' src={article.image} style={{display: 'block', margin: '40px auto'}}/>
                            <p style={{marginTop: '15px'}}>{article.body}</p>
                            <Divider/>
                            <Header as='h3' style={{color: '#37003C'}}>Comments</Header>
                            <Comment.Group >
                                {article.comments.map(comment => {return <NewsComment comment={comment}/>})}
                                <Form reply onSubmit={handleSubmit}>
                                <Form.TextArea value={comment} onChange={(e) => setComment(e.target.value)} style={{height: '70px'}}/>
                                <Button size='tiny' content='Add Comment' labelPosition='left' icon='edit' style={{backgroundColor: '#37003C', color: 'white'}} />
                                <div>{signedIn}</div>
                                <div>{user}</div>
                                </Form>
                            </Comment.Group>
                        </Grid.Column>
                        <GridColumn width={1}/>
                        </Grid>
                    </Segment>
                }
           </Container>
       </Container>
    )
}

export default NewsArticle;
