import { useState } from 'react';
import { Comment, Button, Form } from 'semantic-ui-react';

const NewsComment = (props) => {

    return(
        <Comment>
            <Comment.Content>
                <Comment.Author style={{display: 'inline-block'}}>{props.comment.author}</Comment.Author>
                <Comment.Metadata><span>{props.comment.date}</span></Comment.Metadata>
                <Comment.Text style={{marginTop: '5px'}}><p>{props.comment.comment}</p></Comment.Text>
            </Comment.Content>
        </Comment>
    )
}

export default NewsComment;
