import './App.css';
import React, { useEffect, useState, useRef } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import fixturesData from '../apis/fixturesData';
import useAxios from './useAxios';
import Navbar from './navbar';
import Home from './home';
import LeagueTable from './leagueTable';
import LiveScores from './liveScores';
import News from './news';
import TopScorers from './topScorers';

const App = () => {
    const { data: table, isPending: tablePending, error: tableError } = useAxios('leagueTable/2790', 3600000);
    const { data: todaysGames, isPending: todaysGamesPending, error: todaysGamesError } 
            = useAxios(`fixtures/league/2790/${new Date().toISOString().slice(0,10)}`, 600000);
    const [round, setRound] = useState('');
    const [fixtures, setFixtures] = useState([]);
    const prevRound = useRef(round);

    useEffect(() => {
        if (round !== prevRound.current) {
                fetchFixtures();
            } else {
            fixturesData.get('fixtures/rounds/2790/current').then((res) => {
                let currentRound = res.data.api.fixtures[0].slice(-2);
                if (currentRound[0] === '_') {
                    currentRound = currentRound[1];
                }
                setRound(currentRound);
                prevRound.current = currentRound;
                }).catch(function (error) {
                    console.log(error);
                }).then(() => {
                    fetchFixtures();
                })
        }
    },[round])

    const fetchFixtures = () => {
        fixturesData.get(`fixtures/league/2790/Regular_Season_-_${round}`).then((res) => {
            setFixtures(res.data.api.fixtures);
            }).catch(function (error) {
            console.log(error);
        })
    }

    const updateRound = (round) => {
        setRound(round);
    }

    return(
        <Router>
            <Navbar/>
            <Switch>
                  <Route exact path="/"><Home round={round} fixtures={fixtures} updateRound={updateRound}/></Route>
                  <Route exact path="/livescores"><LiveScores todaysGames={todaysGames} todaysGamesPending={todaysGamesPending} todaysGamesError={todaysGamesError}/></Route>
                  <Route exact path="/leaguetable"><LeagueTable table={table} tablePending={tablePending} tableError={tableError}/></Route>
                  <Route exact path="/news"><News/></Route>
                  <Route exact path="/topscorers"><TopScorers/></Route>
            </Switch>
        </Router>
    )  
}

export default App;


