import './App.css';
import React, { useEffect, useState } from 'react';
import { Container } from 'semantic-ui-react';

import PageHeader from './pageHeader';
import NewsList from './newsList';
import { GoogleAuthContext } from '../apis/googleAuthContext';

const News = () => {
    const { signedIn, user } = useContext(GoogleAuthContext);
    const [news, setNews] = useState(null);

    useEffect(() => {
        fetch('http://localhost:8000/news')
            .then(res => {
                return res.json();
            })
            .then(data => {
                setNews(data);
            });
    }, []);

    return(
       <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                <PageHeader title={'Latest News'}/>
                {news && <NewsList news={news} signedIn={signedIn} user={user} />}
           </Container>
       </Container>
    );
}

export default News;
