import { useContext } from 'react';
import { Comment, Button } from 'semantic-ui-react';
import { GoogleAuthContext } from '../apis/googleAuthContext';
import { format } from 'timeago.js';

const NewsComment = (props) => {
    const { signedIn, userId } = useContext(GoogleAuthContext);

    const deleteButton = () => {
        if(userId === props.comment.authorId && signedIn) {
            return <Button size='mini'>Delete Comment</Button>
        }
    }


    return(
        <Comment style={{marginBottom: '10px'}}>
            <Comment.Content>
                <Comment.Author style={{display: 'inline-block'}}>{props.comment.author}</Comment.Author>
                <Comment.Metadata><span>{format(props.comment.date)}</span></Comment.Metadata>
                <Comment.Text style={{marginTop: '10px'}}><p>{props.comment.newComment}</p></Comment.Text>
                <div>{deleteButton()}</div>
            </Comment.Content>
        </Comment>
    )
}

export default NewsComment;
