import './App.css';
import React from 'react';
import 'semantic-ui-css/semantic.min.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Container, Menu } from 'semantic-ui-react';

import Home from './home';
import LeagueTable from './leagueTable';

class App extends React.Component {
    state = {}

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem } = this.state

        return(
            <Router>
                <Menu inverted size='huge' style={{ backgroundColor: '#37003C', margin: '0rem', borderRadius: '0rem'}}>
                  <Container>
                      <Menu.Item header style={{ color: 'white' }}>PremInfo</Menu.Item>
                      <Menu.Item
                          style={{ color: 'white' }}
                          name='Fixtures/Results'
                          active={activeItem === 'Fixtures/Results'}
                          onClick={this.handleItemClick}
                      >
                      <Link to='/'>Fixtures/Results</Link>
                      </Menu.Item>
                      <Menu.Item
                        style={{ color: 'white' }}
                        name='Table'
                        active={activeItem === 'Table'}
                        onClick={this.handleItemClick}
                      >
                      <Link to='/leaguetable'>Table</Link>
                      </Menu.Item>
                  </Container>
                </Menu>
                <Switch>
                      <Route exact path="/" component={Home} />
                      <Route exact path="/leaguetable" component={LeagueTable} />
                </Switch>
            </Router>
        )  
    }
}

export default App;
