import { useState } from 'react';
import { Comment, Button, Form } from 'semantic-ui-react';

const NewsComment = (props) => {

    return(
        <Comment>
        <Comment.Content>
            <Comment.Author>{props.comment.author}</Comment.Author>
            <Comment.Metadata><span>{props.comment.date}</span></Comment.Metadata>
            <Comment.Text><p>{props.comment.comment}</p></Comment.Text>
            <Comment.Actions><a>reply</a></Comment.Actions>
        </Comment.Content>
        </Comment>
    )
}

export default NewsComment;
