import './App.css';
import React, { useEffect, useState, useRef } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Container, Menu, Image } from 'semantic-ui-react';

import fixturesData from '../apis/fixturesData';
import Home from './home';
import LeagueTable from './leagueTable';
import LiveScores from './liveScores';
import News from './news';
import TopScorers from './topScorers';
import useAxios from './useAxios';

const App = () => {
    // const { data: current, isPending, error } = useAxios('fixtures/rounds/2790/current');
    const {data: table, isPending: tablePending, error: tableError } = useAxios('leagueTable/2790');
    const { data: todaysGames, isPending: todaysGamesPending, error: todaysGamesError } = useAxios(`fixtures/league/2790/${new Date().toISOString().slice(0,10)}`);
    const [round, setRound] = useState('');
    const [fixtures, setFixtures] = useState([]);
    const prevRound = useRef(round);

    // state = {}

    // handleItemClick = (e, { name }) => this.setState({ activeItem: name })
    // const prevRound = useRef(round);

    useEffect(() => {
        if (round !== prevRound.current) {
                fetchFixtures();
            } else {
            fixturesData.get('fixtures/rounds/2790/current').then((res) => {
                let currentRound = res.data.api.fixtures[0].slice(-2);
                if (currentRound[0] === '_') {
                    currentRound = currentRound[1];
                }
                setRound(currentRound);
                prevRound.current = currentRound;
                }).catch(function (error) {
                    console.log(error);
                }).then(() => {
                    fetchFixtures();
                })
        } 
    },[round])

    const fetchFixtures = () => {
        fixturesData.get(`fixtures/league/2790/Regular_Season_-_${round}`).then((res) => {
            setFixtures(res.data.api.fixtures);
            }).catch(function (error) {
            console.log(error);
        })
    }

    const updateRound = (round) => {
        setRound(round);
    }

    // const formatRound = () => {
    //     if (isPending === true || error){
    //         console.log(isPending, error);
    //     } else if (round !== prevRound.current) {
    //         fetchFixtures();
    //     } else {
    //         let currentRound = current.data.api.fixtures[0].slice(-2);
    //         if(currentRound[0] === '_'){
    //             currentRound = currentRound[1];
    //         } 
    //         setRound(currentRound);
    //         prevRound.current = currentRound;
    //     }
    // }

    // const fetchFixtures = () => {
    //     fixturesData.get(`fixtures/league/2790/Regular_Season_-_${round}`).then((res) => {
    //         setFixtures(res.data.api.fixtures);
    //         }).catch(function (error) {
    //         console.log(error);
    //     })
    // }


        // const { activeItem } = this.state

    return(
        <Router>
            <Menu inverted size='huge' style={{ backgroundColor: '#37003C', margin: '0rem', borderRadius: '0rem'}}>
              <Container>
                  <Menu.Item></Menu.Item>
                  <Menu.Item header style={{ color: 'white' }}><Image size='tiny' src='/images/PL-Lion.png'/> PremInfo</Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Live Scores'
                    //   active={activeItem === 'Fixtures/Results'}
                    //   onClick={this.handleItemClick}
                  >
                  <Link to='/livescores'>Live Scores</Link>
                  </Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='News'
                    //   active={activeItem === 'Fixtures/Results'}
                    //   onClick={this.handleItemClick}
                  >
                  <Link to='/news'>News</Link>
                  </Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Fixtures/Results'
                    //   active={activeItem === 'Fixtures/Results'}
                    //   onClick={this.handleItemClick}
                  >
                  <Link to='/'>Fixtures/Results</Link>
                  </Menu.Item>
                  <Menu.Item
                    style={{ color: 'white' }}
                    name='Table'
                    // active={activeItem === 'Table'}
                    // onClick={this.handleItemClick}
                  >
                  <Link to='/leaguetable'>Table</Link>
                  </Menu.Item>
                  <Menu.Item
                      style={{ color: 'white' }}
                      name='Top Scorers'
                    //   active={activeItem === 'Fixtures/Results'}
                    //   onClick={this.handleItemClick}
                  >
                  <Link to='/topscorers'>Top Scorers</Link>
                  </Menu.Item>
              </Container>
            </Menu>
            <Switch>
                  <Route exact path="/"><Home round={round} fixtures={fixtures} updateRound={updateRound}/></Route>
                  <Route exact path="/livescores"><LiveScores todaysGames={todaysGames} todaysGamesPending={todaysGamesPending} todaysGamesError={todaysGamesError}/></Route>
                  <Route exact path="/leaguetable"><LeagueTable table={table} tablePending={tablePending} tableError={tableError}/></Route>
                  <Route exact path="/news"><News/></Route>
                  <Route exact path="/topscorers"><TopScorers/></Route>
            </Switch>
        </Router>
    )  
}

export default App;
