import './App.css';
import React, { useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import { Container, Segment, Grid, Header, Image } from 'semantic-ui-react';
import MatchCard from './matchCard';
import useAxios from './useAxios';

const LiveScores = (props) => {
    console.log(props.todaysGames);    

    return(
       <Container className="App">
           <Container style={{background: 'white', borderStyle: 'solid', borderColor: '#37003C', borderWidth: '3px', borderRadius: '8px', margin: '80px', padding: '10px'}}>
                <Segment style={{margin: '15px'}} textAlign='center'>
                    <Grid>
                        <Grid.Column width={4}/>
                        <Grid.Column width={8}>
                            <Header style={{color: '#37003C'}}>
                                Today's Games
                            </Header>
                            <Image style={{display: 'block', marginLeft: 'auto', marginRight: 'auto'}} src='images/premier-league-3-logo.png'></Image>
                        </Grid.Column>
                        <Grid.Column width={4}/>
                    </Grid>
                </Segment>
                <Container>                  
                    {/* { props.todaysGamesError && <Segment style={{margin: '15px'}} textAlign='center'><div>{props.todaysGamesError}</div></Segment> }
                    { props.todaysGamesPending && <Segment style={{margin: '15px'}} textAlign='center'><div>Loading...</div></Segment> }
                    { !props.todaysGames.data.api.fixtures && <Segment style={{margin: '15px'}} textAlign='center'><div>No games today</div><Link to='/'>Fixtures/Results</Link></Segment>} */}
                    { props.todaysGames.data.api.fixtures && props.todaysGames.data.api.fixtures.map(fixture => {return <MatchCard fixture={fixture}/>})}
                </Container>
            </Container>   
       </Container>
    )  
}

export default LiveScores;
